import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAvailableSlots } from '../hooks/useBookings';
import { useMutation, UseMutationResult } from '@tanstack/react-query';
import { CreateBookingRequest, Slot } from '../types';
import { apiService } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { LoadingSpinner } from '../components/LoadingSpinner';
import { ErrorMessage } from '../components/ErrorMessage';
import { SuccessMessage } from '../components/SuccessMessage';

export const SlotBookingPage: React.FC = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [selectedDate, setSelectedDate] = useState<string>(
    new Date().toISOString().split('T')[0]
  );
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [createdBooking, setCreatedBooking] = useState<any>(null);

  // Fetch available slots
  const { 
    data: slots, 
    isLoading: slotsLoading, 
    error: slotsError,
    refetch: refetchSlots 
  } = useAvailableSlots(selectedDate);

  // Create booking mutation using useMutation directly
  const {
    mutate: createBooking,
    isPending: isCreating,
    error: bookingError,
    isSuccess: bookingSuccess
  }: UseMutationResult<any, unknown, CreateBookingRequest> = useMutation({
    mutationFn: (bookingData: CreateBookingRequest) => apiService.createBooking(bookingData),
    onSuccess: (booking) => {
      setCreatedBooking(booking);
    }
  });

  // Handle slot selection
  const handleSlotSelect = useCallback((slot: Slot) => {
    setSelectedSlot(slot);
  }, []);

  // Handle booking submission
  const handleBookingSubmit = useCallback((e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedSlot || !user?.id) {
      return;
    }

    createBooking({
      slotId: selectedSlot.id,
      customerId: user.id
    });
  }, [selectedSlot, user?.id, createBooking]);

  // Handle date change
  const handleDateChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setSelectedDate(e.target.value);
    setSelectedSlot(null); // Reset selection when date changes
  }, []);

  // Format time for display
  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  // Handle successful booking
  React.useEffect(() => {
    if (bookingSuccess && createdBooking) {
      // Navigate to review page after a short delay
      const timer = setTimeout(() => {
        navigate(`/booking/${createdBooking.id}`);
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [bookingSuccess, createdBooking, navigate]);

  // Redirect to login if not authenticated
  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">
            Please log in to book slots
          </h2>
          <button
            onClick={() => navigate('/login')}
            className="btn-primary"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      {/* Header with user info */}
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 mb-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">
              Book Your Slot
            </h1>
            <p className="text-gray-600 mt-1">
              Welcome back, {user.name}!
            </p>
          </div>
          <div className="flex items-center gap-4">
            <span className="text-sm text-gray-600">
              {user.email}
            </span>
            <button
              onClick={logout}
              className="btn-secondary text-sm"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Date Selection */}
        <div className="card mb-6">
          <label htmlFor="date" className="block text-sm font-medium text-gray-700 mb-2">
            Select Date
          </label>
          <input
            type="date"
            id="date"
            value={selectedDate}
            onChange={handleDateChange}
            min={new Date().toISOString().split('T')[0]}
            className="input max-w-xs"
          />
        </div>

        {/* Error Messages */}
        {slotsError && (
          <ErrorMessage 
            message="Failed to load available slots. Please try again." 
            className="mb-6"
          />
        )}

        {bookingError && (
          <ErrorMessage 
            message={
              typeof bookingError === 'string'
                ? bookingError
                : ((bookingError as any)?.response?.data?.message as string) || 'Failed to create booking. Please try again.'
            }
            className="mb-6"
          />
        )}

        {/* Success Message */}
        {bookingSuccess && (
          <SuccessMessage 
            message="Booking created successfully! Redirecting to review page..." 
            className="mb-6"
          />
        )}

        {/* Available Slots */}
        <div className="card mb-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            Available Slots for {new Date(selectedDate).toLocaleDateString()}
          </h2>

          {slotsLoading ? (
            <LoadingSpinner size="lg" className="py-8" />
          ) : slots && slots.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {slots.map((slot) => (
                <div
                  key={slot.id}
                  className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                    selectedSlot?.id === slot.id
                      ? 'border-primary-500 bg-primary-50'
                      : 'border-gray-200 hover:border-primary-300'
                  }`}
                  onClick={() => handleSlotSelect(slot)}
                >
                  <div className="text-center">
                    <div className="text-lg font-medium text-gray-900">
                      {formatTime(slot.start_time)}
                    </div>
                    <div className="text-sm text-gray-500">
                      to {formatTime(slot.end_time)}
                    </div>
                    {slot.provider && (
                      <div className="text-sm text-gray-600 mt-1">
                        Provider: {slot.provider.name}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              No available slots for the selected date.
            </div>
          )}
        </div>

        {/* Booking Form */}
        {selectedSlot && (
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">
              Complete Your Booking
            </h2>

            <form onSubmit={handleBookingSubmit} className="space-y-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-2">Selected Slot:</h3>
                <p className="text-gray-600">
                  {formatTime(selectedSlot.start_time)} - {formatTime(selectedSlot.end_time)}
                </p>
                {selectedSlot.provider && (
                  <p className="text-gray-600">
                    Provider: {selectedSlot.provider.name}
                  </p>
                )}
              </div>

              <div className="bg-blue-50 p-4 rounded-lg">
                <h3 className="font-medium text-blue-900 mb-2">Booking as:</h3>
                <p className="text-blue-700">
                  {user.name} ({user.email})
                </p>
              </div>

              <div className="flex gap-4">
                <button
                  type="submit"
                  disabled={isCreating}
                  className="btn-primary flex-1 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isCreating ? (
                    <div className="flex items-center justify-center">
                      <LoadingSpinner size="sm" className="mr-2" />
                      Creating Booking...
                    </div>
                  ) : (
                    'Confirm Booking'
                  )}
                </button>

                <button
                  type="button"
                  onClick={() => setSelectedSlot(null)}
                  className="btn-secondary"
                >
                  Change Slot
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};
